import React,{useState, useEffect} from 'react';
import './App.css';

function App() {
const Apid = 'https://jsonplaceholder.typicode.com/'
const [req, setReq] = useState('users')
const [items, setItems] = useState([])

useEffect(()=>{
  const fetchItems = async() =>{
 try{
   const res = await fetch(`${Apid}${req}`)
   const data = await res.json()
   setItems(data)
 }catch(err){
   console.log(err)
 }
  }
  fetchItems()

}, [req])

  return (
    <div className="App">
        <Form req = {req} setReq = {setReq} />

    </div>
  );
}

export default App;
